package com.util;import java.util.ArrayList;import java.util.List;/** * 类描述: * <p/> * BY leo.lin * <p/> * 时间:2016/10/31-14:21 */public class PageListUtil {    /**     *     * @param page 当前页码     * @param totalPage 总页码     * @return     */    public static List<String> getListPg(Integer page,Integer totalPage){        List<String> listpg = new ArrayList<String>();        int length = 5;        if(totalPage-page<5){            length = totalPage-page;        }        if(totalPage == 1){            listpg.add(1+"");        }else{            length += 1;        }        if(page > 2){            listpg.add((page-2)+"");        }        if(page > 1){            listpg.add((page-1)+"");        }        for(int i=0;i<length;i++){            listpg.add((page+i)+"");        }        if(page+5<totalPage){            listpg.add((page+6)+"");        }        return listpg;    }}